-- ----------------------------
-- Table structure for tintuc
-- ----------------------------
DROP TABLE IF EXISTS `tintuc`;
CREATE TABLE `tintuc`  (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `tieude` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `tomtat` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `noidung` longtext CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `hinh` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `loai_tin_id` int(10) NOT NULL,
  `nguoi_dang` int(10) NOT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `nguoi_dung_id`(`nguoi_dang`) USING BTREE,
  INDEX `loai_tin_id`(`loai_tin_id`) USING BTREE,
  CONSTRAINT `tintuc_ibfk_1` FOREIGN KEY (`nguoi_dang`) REFERENCES `nguoidung` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `tintuc_ibfk_2` FOREIGN KEY (`loai_tin_id`) REFERENCES `loaitin` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 79 CHARACTER SET = utf8 COLLATE = utf8_unicode_ci ROW_FORMAT = Compact;

-- ----------------------------
-- Records of tintuc
-- ----------------------------
INSERT INTO `tintuc` VALUES (31, 'Giới Thiệu Về Mầm Non Vành Khuyên', '<p><span style=\"font-size:14px\"><span style=\"color:#96bf04\">Ch&uacute;ng t&ocirc;i &yacute; thức rằng những năm từ 0 đến 5&nbsp;tuổi l&agrave; những năm th&aacute;ng &ldquo;v&agrave;ng&rdquo;</span></span></p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i t&ocirc;n trọng những gi&aacute; trị c&aacute; nh&acirc;n, b&ecirc;n cạnh đ&oacute; nu&ocirc;i dưỡng t&acirc;m l&yacute; ham th&iacute;ch đến trường, biết chia sẻ, năng động v&agrave; tự tin v&agrave;o bản th&acirc;n của trẻ.</p>\r\n', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://localhost:8080/vanhkhuyen/admin/images/tintuc/hinhanhbaiviet/images/mo-truong-mam-non-tu-thuc-min-4.jpg\" style=\"height:235px; width:413px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div style=\"page-break-after:always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Giai đoạn đầu đời của trẻ em v&ocirc; c&ugrave;ng quan trọng, c&oacute; ảnh hưởng trực tiếp đến t&iacute;nh c&aacute;ch v&agrave; sự ph&aacute;t triển sau n&agrave;y của trẻ. Ch&iacute;nh v&igrave; thế <span style=\"color:#ff6600\"><strong>Trường mầm non V&agrave;nh Khuy&ecirc;n</strong></span> cố gắng x&acirc;y dựng một m&ocirc;i trường học tập v&agrave; sinh hoạt khoa học nhưng cũng rất th&acirc;n thiện, nhằm đảm bảo cho trẻ c&oacute; một hướng ph&aacute;t triển đ&uacute;ng đắn.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://localhost:8080/vanhkhuyen/admin/images/tintuc/hinhanhbaiviet/images/saigon-academy-co-nen-cho-con-di-nha-tre-mua-dich-benh-4.jpg\" style=\"height:235px; width:563px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Ch&uacute;ng t&ocirc;i t&ocirc;n trọng những gi&aacute; trị c&aacute; nh&acirc;n, b&ecirc;n cạnh đ&oacute; nu&ocirc;i dưỡng t&acirc;m l&yacute; ham th&iacute;ch đến trường, biết chia sẻ, năng động v&agrave; tự tin v&agrave;o bản th&acirc;n của trẻ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Ch&uacute;ng t&ocirc;i c&oacute; đội ngũ gi&aacute;o vi&ecirc;n cao cấp về nghiệp vụ v&agrave; tận t&acirc;m với nghề nghiệp, c&oacute; một m&ocirc;i trường sinh hoạt vệ sinh v&agrave; thoải m&aacute;i, chương tr&igrave;nh học tập v&agrave; dinh dưỡng ph&ugrave; hợp với từng c&aacute; nh&acirc;n, x&acirc;y dựng những buổi học v&agrave; ngoại kh&oacute;a thật sự bổ &iacute;ch.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '44052748121_11ea019d5e_c.jpg', 84, 21);
INSERT INTO `tintuc` VALUES (44, 'Tuyển Giáo Viên Mầm Non (2019)', '<p>Hạn nộp 15-1-2019</p>\r\n', '<p>M&ocirc; tả&nbsp;&nbsp; &nbsp;<br />\r\n- X&acirc;y dựng gi&aacute;o &aacute;n/b&agrave;i giảng v&agrave; kế hoạch giảng day<br />\r\n- Thực hiện nu&ocirc;i dưỡng, chăm s&oacute;c v&agrave; gi&aacute;o dục trẻ</p>\r\n\r\n<p>Y&ecirc;u cầu&nbsp;&nbsp; &nbsp;<br />\r\n- Tốt nghiệp Cao đẳng/Đại học chuy&ecirc;n ng&agrave;nh gi&aacute;o dục Mầm non<br />\r\n- Giọng n&oacute;i chuẩn, t&aacute;c phong sư phạm<br />\r\n- Ch&uacute; trọng chất lượng dịch vụ v&agrave; hướng tới kết quả cuối c&ugrave;ng trong c&ocirc;ng việc;<br />\r\n- Phẩm chất đạo đức tốt, lối sống l&agrave;nh mạnh trong s&aacute;ng của nh&agrave; gi&aacute;o;<br />\r\n- Y&ecirc;u trẻ; t&acirc;m huyết với gi&aacute;o dục; cống hiến v&agrave; sẵn s&agrave;ng nhận nhiệm vụ được giao ph&oacute;;<br />\r\n- Chủ động, trung thực, tu&acirc;n thủ, c&ocirc;ng t&acirc;m, hợp t&aacute;c, c&oacute; tinh thần tr&aacute;ch nhiệm v&agrave; chịu được &aacute;p lực trong c&ocirc;ng việc;</p>\r\n\r\n<p>Quyền lợi&nbsp;&nbsp; &nbsp;<br />\r\n- Thưởng lương th&aacute;ng 13, xem x&eacute;t tăng lương mỗi năm học<br />\r\n- Thưởng th&agrave;nh t&iacute;ch cuối năm<br />\r\n- Kh&aacute;m sức khỏe định kỳ tại bệnh viện Quốc Tế Phương Ch&acirc;u</p>\r\n\r\n<p>Hạn nộp&nbsp;&nbsp;&nbsp; 15-1-2019</p>\r\n', 'tuyển dụng.jpg', 98, 21);
INSERT INTO `tintuc` VALUES (46, 'Chính Sách Nhà Trường ', '<p>Ch&iacute;nh s&aacute;ch miễn giảm cho học sinh trường V&agrave;nh Khuy&ecirc;n (2018-2019)</p>\r\n', '<p style=\"margin-left:0px; margin-right:0px; text-align:left\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Theo quy định tại&nbsp;<a href=\"https://thuvienphapluat.vn/van-ban/Thue-Phi-Le-Phi/Nghi-dinh-86-2015-ND-CP-co-che-thu-quan-ly-hoc-phi-co-so-giao-duc-quoc-dan-nam-hoc-2015-2016-den-2020-2021-292146.aspx\" style=\"margin: 0px; padding: 0px; color: rgb(26, 13, 171); text-decoration: none;\" target=\"_blank\">Nghị định 86/2015/NĐ-CP</a>&nbsp;quy định về cơ chế thu, quản l&yacute; học ph&iacute; đối với cơ sở gi&aacute;o dục thuộc hệ thống gi&aacute;o dục quốc d&acirc;n v&agrave; ch&iacute;nh s&aacute;ch miễn, giảm học ph&iacute;, hỗ trợ chi ph&iacute; học tập từ năm học 2015 - 2016 đến năm học 2020 &ndash; 2021 th&igrave; đối tượng trẻ em bậc mầm non chỉ được miễn giảm học ph&iacute; nếu đ&aacute;p ứng điều kiện quy định tại Điều 7, Điều 8 Nghị định n&agrave;y. Cụ thể:</span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><em>&ldquo;</em><em>Điều 7. Đối tượng được miễn học ph&iacute;</em></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><em>2.&nbsp;</em><em>Trẻ em học mẫu gi&aacute;o v&agrave; học sinh, sinh vi&ecirc;n bị t&agrave;n tật, khuyết tật c&oacute; kh&oacute; khăn về kinh tế.</em></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><em>3.&nbsp;</em><em>Trẻ em học mẫu gi&aacute;o v&agrave; học sinh dưới 16 tuổi kh&ocirc;ng c&oacute; nguồn nu&ocirc;i dưỡng quy định tại&nbsp;Khoản 1 Điều 5 Nghị định số 136/2013/NĐ-CP&nbsp;ng&agrave;y 21 th&aacute;ng 10 năm 2013 của Ch&iacute;nh phủ quy định ch&iacute;nh s&aacute;ch trợ gi&uacute;p x&atilde; hội đối với đối tượng bảo trợ x&atilde; hội.</em></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><em>4.&nbsp;</em><em>Trẻ em học mẫu gi&aacute;o v&agrave; học sinh phổ th&ocirc;ng c&oacute; cha mẹ thuộc diện hộ ngh&egrave;o theo quy định của Thủ tướng Ch&iacute;nh phủ.</em></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><em>5.&nbsp;</em><em>Trẻ em học mẫu gi&aacute;o v&agrave; học sinh phổ th&ocirc;ng l&agrave; con của hạ sĩ quan v&agrave; binh sĩ, chiến sĩ đang phục vụ c&oacute; thời hạn trong lực lượng vũ trang nh&acirc;n d&acirc;n.</em></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><em>Điều 8. Đối tượng được giảm học ph&iacute;</em></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><em>1. C&aacute;c đối tượng được giảm 70% học ph&iacute; gồm:</em></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><em>c)&nbsp;</em><em>Trẻ em học mẫu gi&aacute;o v&agrave; học sinh, sinh vi&ecirc;n l&agrave; người d&acirc;n tộc thiểu số (kh&ocirc;ng phải l&agrave; d&acirc;n tộc thiểu số rất &iacute;t người) ở v&ugrave;ng c&oacute; điều kiện kinh tế - x&atilde; hội đặc biệt kh&oacute; khăn theo quy định của cơ quan c&oacute; thẩm quyền.</em></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><em>2.&nbsp;</em><em>C&aacute;c đối tượng được giảm 50% học ph&iacute; gồm:</em></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><em>a)&nbsp;</em><em>Trẻ em học mẫu gi&aacute;o v&agrave; học sinh, sinh vi&ecirc;n l&agrave; con c&aacute;n bộ, c&ocirc;ng nh&acirc;n, vi&ecirc;n chức m&agrave; cha hoặc mẹ bị tai nạn lao động hoặc mắc bệnh nghề nghiệp được hưởng trợ cấp thường xuy&ecirc;n;</em></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\"><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><em>b)&nbsp;</em><em>Trẻ em học mẫu gi&aacute;o v&agrave; học sinh phổ th&ocirc;ng c&oacute; cha mẹ thuộc hộ cận ngh&egrave;o theo quy định của Thủ tướng Ch&iacute;nh phủ.&rdquo;</em></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'miengiam.jpg', 84, 21);
INSERT INTO `tintuc` VALUES (48, 'Cơ Cấu Tổ Chức Trường Mầm Non Vành Khuyên', '', '<p><span style=\"color:#3498db\">1. Đ/c Nguyễn Minh Nhựt &ndash; Hiệu trưởng:</span></p>\r\n\r\n<p>- Thực hiện nhiệm vụ, quyền hạn theo quy định của điều lệ trường MN:</p>\r\n\r\n<p>+ Cụ thể: Phụ tr&aacute;ch c&aacute;c mặt c&ocirc;ng t&aacute;c, tổ chức, kế hoạch, thi đua, CSVC, CSND, Kiểm tra nội bộ, ph&aacute;p chế...</p>\r\n\r\n<p>- Chịu tr&aacute;ch nhiệm tổ chức, quản l&yacute;, điều h&agrave;nh c&aacute;c HĐ của nh&agrave; trường. X&acirc;y dựng v&agrave; tổ chức thực hiện kế hoạch năm học. Th&agrave;nh lập v&agrave; cử c&aacute;c tổ trưởng c&aacute;c tổ chuy&ecirc;n m&ocirc;n, tổ h&agrave;nh ch&iacute;nh quản trị, th&agrave;nh lập c&aacute;c hội đồng trong trường.</p>\r\n\r\n<p>- Quản l&yacute; h&agrave;nh ch&iacute;nh, t&agrave;i ch&iacute;nh, t&agrave;i sản của nh&agrave; trường, tổ chức thực hiện quy chế d&acirc;n chủ trong nh&agrave; trường.</p>\r\n\r\n<p>&nbsp; - Ph&acirc;n c&ocirc;ng quản l&yacute;, kiểm tra c&ocirc;ng t&aacute;c của GV,NV. Đề nghị khen thưởng kỷ luật, đảm bảo quyền lợi của gi&aacute;o vi&ecirc;n, nh&acirc;n vi&ecirc;n theo qui định của nh&agrave; nước.</p>\r\n\r\n<p>&nbsp;- Tham mưu đề xuất với c&aacute;c cấp, c&aacute;c ng&agrave;nh x&acirc;y dựng trường nh&agrave; trường, huy động XHHGD bằng mọi nguồn lực phục vụ cho c&ocirc;ng t&aacute;c CSGD trẻ,&nbsp; sự ph&aacute;t triển gi&aacute;o dục MN của phường.</p>\r\n\r\n<p>- Tham gia 2 hoạt động chăm s&oacute;c, gi&aacute;o dục trẻ/tuần.</p>\r\n\r\n<p>- Chủ t&agrave;i khoản - Chủ tịch hội đồng thi đua khen thưởng.</p>\r\n\r\n<p><span style=\"color:#3498db\">2. Đ/c Nguyễn Thị Yến Ly &ndash; Ph&oacute; hiệu trưởng phụ tr&aacute;ch chuy&ecirc;n m&ocirc;n:</span></p>\r\n\r\n<p>- Thực hiện v&agrave; chịu tr&aacute;ch nhiệm trước Hiệu trưởng về những việc được ph&acirc;n c&ocirc;ng.</p>\r\n\r\n<p>- L&agrave; người phụ tr&aacute;ch chuy&ecirc;n m&ocirc;n, chịu tr&aacute;ch nhiệm về c&ocirc;ng t&aacute;c chỉ đạo chuy&ecirc;n m&ocirc;n trong nh&agrave; trường. Thực hiện kế hoạch chỉ đạo của hiệu trưởng. Thay mặt hiệu trưởng điều h&agrave;nh c&aacute;c hoạt động của nh&agrave; trường khi được uỷ quyền, ph&acirc;n c&ocirc;ng của Hiệu trưởng.</p>\r\n\r\n<p>- Chủ đ&ocirc;ng tham mưu với Hiệu trưởng c&aacute;c giải ph&aacute;p nhằm thực hiện tốt c&aacute;c mặt c&ocirc;ng t&aacute;c được ph&acirc;n c&ocirc;ng.</p>\r\n\r\n<p>- C&ugrave;ng Hiệu trưởng chịu tr&aacute;ch nhiệm trước cấp tr&ecirc;n về c&aacute;c hoạt động li&ecirc;n quan của nh&agrave; trường.</p>\r\n\r\n<p>* Trực tiếp phụ tr&aacute;ch: C&ocirc;ng t&aacute;c chuy&ecirc;n m&ocirc;n.</p>\r\n\r\n<p>+ X&acirc;y dựng kế hoạch chỉ đạo chuy&ecirc;n m&ocirc;n trong năm, th&aacute;ng, tuần b&aacute;o c&aacute;o cụ thể về nội dung chỉ đạo chuy&ecirc;n m&ocirc;n, c&aacute;c giải ph&aacute;p thực hiện trước cuộc họp giao ban &ldquo; BGH v&agrave; c&aacute;c khối trưởng chuy&ecirc;n m&ocirc;n&rdquo;.</p>\r\n\r\n<p>+ Bồi dưỡng chuy&ecirc;n m&ocirc;n theo chương tr&igrave;nh GDMN. Phụ tr&aacute;ch mảng KT nội bộ, khảo th&iacute; nghi&ecirc;n cứu khoa học, CNTT, Ng&acirc;n h&agrave;ng b&agrave;i tập KSCL, hoạt động ngoại kho&aacute;.</p>\r\n\r\n<p>- Tham mưu tổ chức c&aacute;c hoạt động CM, ph&aacute;t động, KT đ&aacute;nh gi&aacute; c&aacute;c đợt thi đua, thi tay nghề GV trong nh&agrave; trường. B&aacute;o c&aacute;o kết quả h&agrave;ng th&aacute;ng theo kế hoạch.</p>\r\n\r\n<p>+ Tổ chức hoạt động ng&agrave;y hội, ng&agrave;y lễ, chuy&ecirc;n đề, ngoại kh&oacute;a.</p>\r\n\r\n<p>+ X&acirc;y dựng v&agrave; tổ chức Sơ kết 2 năm thực hiện chuy&ecirc;n đề &ldquo;Gi&aacute;o dục vệ sinh chăm s&oacute;c v&agrave; đảm bảo an to&agrave;n cho trẻ &rdquo;.</p>\r\n\r\n<p>+ Hồ sơ SS trường chuẩn Quốc gia, trường đạt chuẩn KĐCLGD.</p>\r\n\r\n<p>+Tham gia 4 hoạt động gi&aacute;o dục trẻ/tuần.</p>\r\n\r\n<p>* Phụ tr&aacute;ch c&ocirc;ng t&aacute;c phổ cập.</p>\r\n\r\n<p>- X&acirc;y dựng kế hoạch tổ chức thực hiện, l&agrave;m b&aacute;o c&aacute;o thống k&ecirc;, đi họp nắm bắt việc chỉ đạo để triển khai kịp thời. C&oacute; đầy đủ hồ sơ phổ cập theo quy định</p>\r\n\r\n<p>- Tham mưu v&agrave; Triển khai c&ocirc;ng t&aacute;c PCGD v&agrave; PCGD TH ĐĐT đảm bảo phục vụ tốt cho việc ho&agrave;n th&agrave;nh phổ cập gi&aacute;o dục trẻ em 5 tuổi của Phường Đằng Hải v&agrave; Quận Hải An.</p>\r\n\r\n<p>- C&oacute; đầy đủ c&aacute;c loại hồ sơ phục vụ cho c&ocirc;ng t&aacute;c phổ cập, đảm bảo ch&iacute;nh x&aacute;c, cập nhật, r&otilde; r&agrave;ng, sạch đẹp. Nội dung c&aacute;c hồ sơ được thực hiện đ&uacute;ng theo sự chỉ đạo của Ban chỉ đạo phổ cập c&aacute;c cấp.</p>\r\n\r\n<p>- Theo d&otilde;i thường xuy&ecirc;n, ch&iacute;nh x&aacute;c học sinh chuyển đi, chuyển đến, v&agrave; thường xuy&ecirc;n cập nhật c&aacute;c số liệu phổ cập của địa phương đảm bảo đ&uacute;ng số liệu điều tra phổ cập.</p>\r\n\r\n<p>- Thực hiện đạt chuẩn Phổ cập trẻ em 5 Tuổi.</p>\r\n\r\n<p><span style=\"color:#3498db\"><span style=\"background-color:null\">3. Đ/c Trần Thị Linh &ndash; Chủ tịch c&ocirc;ng đo&agrave;n.</span></span></p>\r\n\r\n<p>- X&acirc;y dựng ti&ecirc;u ch&iacute; thi đua, kế hoạch hoạt động của c&ocirc;ng đo&agrave;n trong năm học, đảm bảo t&iacute;nh d&acirc;n chủ t&iacute;nh hợp ph&aacute;p, bảo vệ quyền lợi hợp ph&aacute;p ch&iacute;nh đ&aacute;ng cho CBGVCNV (Nghi&ecirc;n cứu c&aacute;c văn bản, thực hiện theo đ&uacute;ng qui định).</p>\r\n\r\n<p>- Tham mưu đề xuất &yacute; kiến, t&igrave;m c&aacute;c giải ph&aacute;p tốt tổ chức c&aacute;c hoạt động x&acirc;y dựng nh&agrave;&nbsp; trường đ&aacute;p ứng y&ecirc;u cầu nhiệm vụ năm học. C&ugrave;ng hiệu trưởng x&acirc;y dựng v&agrave; ph&acirc;n c&ocirc;ng nhiệm vụ CBGVCNV, triển khai thực hiện v&agrave; chịu tr&aacute;ch nhiệm về c&ocirc;ng t&aacute;c bảo vệ ch&iacute;nh trị nội bộ nh&agrave; trường.</p>\r\n\r\n<p><span style=\"color:#3498db\">4. Đ/c Phạm Thị Thanh Quỳnh, Vũ Thị Phương&ndash; Tổ trưởng.</span></p>\r\n\r\n<p>- C&oacute; nhiệm vụ x&acirc;y dựng kế hoạch hoạt động chung của khối, kế hoạch chuy&ecirc;n m&ocirc;n, chuy&ecirc;n đề, kế hoạch năm học, kế hoạch th&aacute;ng, gi&uacute;p gi&aacute;o vi&ecirc;n trong khối tổ chức c&aacute;c hoạt động, trao đổi kinh nghiệm về chuy&ecirc;n m&ocirc;n. Tham gia kiểm tra đ&aacute;nh gi&aacute; chất lượng gi&aacute;o vi&ecirc;n, học sinh khối m&igrave;nh phụ tr&aacute;ch. L&ecirc;n tiết mẫu chuy&ecirc;n đề cấp trường, cấp quận do hiệu ph&oacute; ph&acirc;n c&ocirc;ng điều h&agrave;nh.</p>\r\n\r\n<p>- Khối trưởng được tham gia đề xuất trong kế hoạch chuy&ecirc;n m&ocirc;n khen thưởng, kỷ luật đối với gi&aacute;o.</p>\r\n\r\n<p>- Sinh hoạt chuy&ecirc;n m&ocirc;n theo định kỳ, h&agrave;ng th&aacute;ng giao ban giữa hiệu ph&oacute; chuy&ecirc;n m&ocirc;n v&agrave; khối trưởng theo nghị quyết chuy&ecirc;n m&ocirc;n. Giữa khối trưởng v&agrave; gi&aacute;o vi&ecirc;n trong khối phải thống nhất x&acirc;y dựng kế hoạch mạng chủ điểm. Chịu tr&aacute;ch nhiệm trước Hiệu trưởng v&agrave; HPCM đối với c&ocirc;ng việc được giao.</p>\r\n\r\n<p><span style=\"color:#3498db\">5. Đ/c Nguyễn Thị Huyền - Nh&acirc;n vi&ecirc;n văn thư, thủ quỹ, Quản trị mạng. Đ/c Nguyễn Thị Thanh, nh&acirc;n vi&ecirc;n yte l&agrave;m thay c&ocirc;ng t&aacute;c văn thư, thủ quĩ học k&igrave; I cho đ/c Nguyễn Thị Huyền nghỉ thai sản.</span></p>\r\n\r\n<p>- L&agrave;m c&ocirc;ng t&aacute;c văn ph&ograve;ng.</p>\r\n\r\n<p>- Cập nhật th&ocirc;ng tin tr&ecirc;n mạng, b&aacute;o c&aacute;o kịp thời với Ban gi&aacute;m hiệu về nội dung, y&ecirc;u cầu của c&aacute;c th&ocirc;ng tin đ&oacute;.</p>\r\n\r\n<p>- Tham mưu đầu tư n&acirc;ng cấp Website v&agrave; đường truyền Internet trong nh&agrave; trường, thực hiện hiệu quả hoạt động Website v&agrave; hệ thống thư điện tử miền ri&ecirc;ng.</p>\r\n\r\n<p>- Đảm bảo th&ocirc;ng tin th&ocirc;ng suốt giữa nh&agrave; trường với c&aacute;c cơ quan c&oacute; li&ecirc;n quan. Quản l&yacute;, sử dụng con dấu đ&uacute;ng qui định.</p>\r\n\r\n<p>- Theo d&otilde;i, lưu giữ, chuyển giao kịp thời c&aacute;c c&ocirc;ng văn đi, đến theo quy định về c&ocirc;ng t&aacute;c văn ph&ograve;ng.</p>\r\n\r\n<p>-&nbsp; Quản l&yacute; hồ sơ PMIS, EMIS Hồ sơ nh&agrave; trường, nhận c&ocirc;ng văn đến v&agrave; đi đ&uacute;ng theo qui định về thời gian, địa điểm, tổ chức. Tham gia c&aacute;c ng&agrave;y hội, ng&agrave;y lễ do hiệu trưởng điều động ph&acirc;n c&ocirc;ng.</p>\r\n\r\n<p>- Trực tiếp quản l&yacute; vi t&iacute;nh, l&ecirc;n kế hoạch, vệ sinh, bảo tr&igrave;, bảo dưỡng m&aacute;y thường xuy&ecirc;n ở ph&ograve;ng vi t&iacute;nh v&agrave; m&aacute;y t&iacute;nh ở c&aacute;c ph&ograve;ng, c&aacute;c lớp, kịp thời ph&aacute;t hiện những sự cố của m&aacute;y, điều tra nguy&ecirc;n nh&acirc;n hỏng, kịp thời b&aacute;o c&aacute;o hiệu trưởng.</p>\r\n\r\n<p>- L&ecirc;n kế hoạch thay thế những phần mềm cần thiết b&aacute;o c&aacute;o kế to&aacute;n để c&oacute; kế hoạch t&agrave;i ch&iacute;nh.</p>\r\n\r\n<p>- Thực hiện trực mạng theo quy định của PGD, th&ocirc;ng tin tr&ecirc;n trang Web của nh&agrave; trường đa dạng, phong ph&uacute;, ch&iacute;nh x&aacute;c, kịp thời đ&uacute;ng qui định.</p>\r\n\r\n<p>- C&oacute; đủ c&aacute;c loại hồ sơ, sổ s&aacute;ch c&aacute;c loại quỹ theo quy định.</p>\r\n\r\n<p>- Chịu tr&aacute;ch nhiệm về to&agrave;n bộ tiền của trường m&agrave; m&igrave;nh giữ, nếu mất phải đền. Thường xuy&ecirc;n đối chiếu số liệu với sổ kế to&aacute;n. H&agrave;ng th&aacute;ng tiến h&agrave;nh kiểm quỹ dưới sự gi&aacute;m s&aacute;t của hiệu trưởng v&agrave; kế to&aacute;n, c&oacute; bi&ecirc;n bản kiểm quỹ theo quy định.</p>\r\n\r\n<p>- H&agrave;ng th&aacute;ng thu c&aacute;c khoản, tổng hợp, b&aacute;o c&aacute;o số lượng tiền mặt thu về v&agrave; số dư quỹ để kế to&aacute;n b&aacute;o c&aacute;o hiệu trưởng.</p>\r\n\r\n<p>- Thu v&agrave; quản l&yacute; c&aacute;c loại quỹ, c&ugrave;ng kế to&aacute;n c&acirc;n đối quỹ thu chi.</p>\r\n\r\n<p>- Mọi vấn đề thu, chi phải tu&acirc;n thủ theo đ&uacute;ng nguy&ecirc;n tắc, ph&aacute;i c&oacute; giấy của Hiệu trưởng mới được chi tiền. Nếu l&agrave;m sai phải ho&agrave;n to&agrave;n chịu tr&aacute;nh nhiệm.</p>\r\n\r\n<p>- Khi ph&aacute;t tiền, thủ quỹ y&ecirc;u cầu người lĩnh tiền phải k&yacute; v&agrave;o chứng từ kế to&aacute;n v&agrave; k&yacute; v&agrave;o sổ chi của thủ quỹ (ch&uacute; &yacute; kh&ocirc;ng cho nhận tiền thay nếu kh&ocirc;ng được uỷ quyền).</p>\r\n\r\n<p>- Chịu tr&aacute;ch nhiệm trước hiệu trưởng v&agrave; c&aacute;c cơ quan nghiệp vụ cấp tr&ecirc;n về việc thực hiện nghi&ecirc;m chỉnh chế độ hiện h&agrave;nh.</p>\r\n\r\n<p><span style=\"color:#3498db\">6. Đ/c Nguyễn Thị Lan Anh &ndash; Kế to&aacute;n.</span></p>\r\n\r\n<p>- Quản l&yacute; c&aacute;c hoạt động của tổ văn ph&ograve;ng (c&oacute; kế hoạch của cả năm, học kỳ, từng th&aacute;ng). C&oacute; đầy đủ hồ sơ của tổ văn ph&ograve;ng, quản l&yacute; tổ chức hoạt động đều đặn, thường xuy&ecirc;n, theo d&otilde;i ng&agrave;y c&ocirc;ng, giờ c&ocirc;ng, xếp loại thi đua của tổ, tham gia mọi hoạt động kh&aacute;c.</p>\r\n\r\n<p>- Tham mưu với Hiệu trưởng về c&ocirc;ng t&aacute;c t&agrave;i ch&iacute;nh v&agrave; triển khai theo đ&uacute;ng quy định. Đảm bảo việc thực hiện đ&uacute;ng, kịp thời chế độ ch&iacute;nh s&aacute;ch đối với CBGVNV v&agrave; học sinh.</p>\r\n\r\n<p>- Chịu tr&aacute;ch nhiệm với Hiệu trưởng về to&agrave;n bộ hồ sơ, sổ s&aacute;ch, chứng từ thu chi: khoa học, ch&iacute;nh x&aacute;c, hợp l&yacute;, đ&uacute;ng luật.</p>\r\n\r\n<p>- H&agrave;ng th&aacute;ng, h&agrave;ng qu&yacute; phải đảm bảo b&aacute;o c&aacute;o kịp thời về thanh quyết to&aacute;n theo quy định v&agrave; phải b&aacute;o c&aacute;o với Hiệu trưởng về t&igrave;nh h&igrave;nh thu chi t&agrave;i ch&iacute;nh của trường theo từng mục, từng khoản. Cập nhật h&agrave;ng ng&agrave;y về t&igrave;nh h&igrave;nh thu, chi.</p>\r\n\r\n<p>- Tham mưu c&ugrave;ng Hiệu trưởng về việc tu sửa mua sắm cơ sở vật chất của trường.</p>\r\n\r\n<p>- Chịu tr&aacute;ch nhiệm trước Hiệu trưởng về mọi hoạt động li&ecirc;n quan đến t&agrave;i ch&iacute;nh.</p>\r\n\r\n<p>- Chịu tr&aacute;ch nhiệm đ&ocirc;n đốc c&aacute;c khoản thu theo quy định.</p>\r\n\r\n<p>- Gi&uacute;p hiệu trưởng x&acirc;y dựng kế hoạch thu chi t&agrave;i ch&iacute;nh, qui chế chi ti&ecirc;u nội bộ trong nh&agrave; trường.</p>\r\n\r\n<p>- Ho&agrave;n th&agrave;nh nhiệm vụ kế to&aacute;n.</p>\r\n\r\n<p>- Kiểm tra v&agrave; ho&agrave;n thiện chứng từ thu chi t&agrave;i ch&iacute;nh tr&igrave;nh Hiệu trưởng ph&ecirc; duyệt.</p>\r\n\r\n<p>- Chịu tr&aacute;ch nhiệm trước hiệu trưởng v&agrave; c&aacute;c cơ quan nghiệp vụ cấp tr&ecirc;n về việc thực hiện nghi&ecirc;m chỉnh chế độ kế to&aacute;n hiện h&agrave;nh.</p>\r\n\r\n<p>- Ph&aacute;n &aacute;nh, ghi ch&eacute;p kịp thời, trung thực, ch&iacute;nh x&aacute;c c&aacute;c khoản thu chi của nh&agrave; trường. Bảo quản lưu trữ hồ sơ sổ s&aacute;ch, chứng từ kế to&aacute;n gọn g&agrave;ng, khoa học theo đ&uacute;ng thời gian quy định.</p>\r\n\r\n<p>- L&agrave; người chịu tr&aacute;ch nhiệm ch&iacute;nh về x&acirc;y dựng thực đơn (từ chiều h&ocirc;m trước). H&agrave;ng ng&agrave;y theo d&otilde;i số trẻ ăn, tiền ăn t&iacute;nh c&acirc;n đối, điều chỉnh ngay lượng tiền v&agrave; định lượng dinh dưỡng ăn cho trẻ, quyết định loại lượng thực phẩm bổ sung. V&agrave;o sổ t&iacute;nh ăn, l&ecirc;n bảng t&agrave;i ch&iacute;nh c&ocirc;ng khai trong to&agrave;n trường v&agrave; c&aacute;c khoản thu với phụ huynhh.</p>\r\n\r\n<p>- B&aacute;o c&aacute;o c&ocirc;ng khai số tiền thu chi h&agrave;ng th&aacute;ng một c&aacute;ch kịp thời cụ thể r&otilde; r&agrave;ng trước hiệu trưởng.</p>\r\n\r\n<p>- Trước khi nộp tiền kho bạc, kế to&aacute;n phải lập kế hoạch b&aacute;o c&aacute;o Hiệu trưởng kh&ocirc;ng được tự động điều thủ quỹ đi nộp tiền.</p>\r\n\r\n<p>-&nbsp; Đảm bảo đầy đủ c&aacute;c loại hồ sơ sổ s&aacute;ch theo y&ecirc;u cầu, tham mưu với hiệu trưởng trong c&ocirc;ng t&aacute;c c&ocirc;ng khai, sử dụng nguồn XHH v&agrave; nguồn ng&acirc;n s&aacute;ch được cấp.</p>\r\n\r\n<p><span style=\"color:#3498db\">7. Đ/c Nguyễn Thị Sao Mai &ndash; Bếp trưởng.</span></p>\r\n\r\n<p>- L&agrave; người ph&acirc;n c&ocirc;ng điều h&agrave;nh tổ nu&ocirc;i, ghi sổ giao nhận thực phẩm, kiểm tra thực phẩm, đ&ocirc;n đốc nhắc nhở nh&acirc;n vi&ecirc;n vệ sinh ph&ograve;ng bếp, đồ d&ugrave;ng dụng cụ của nh&agrave; bếp, đảm bảo tuyệt đối, QTCB, VS an to&agrave;n thực phẩm. C&ocirc;ng khai định lượng, đảm bảo đủ lượng, đủ chất hợp l&yacute;.</p>\r\n\r\n<p>- Đảm bảo giờ giấc ăn uống theo chế độ sinh hoạt h&agrave;ng ng&agrave;y. C&oacute; kế hoạch h&agrave;ng th&aacute;ng về c&ocirc;ng việc được ph&acirc;n c&ocirc;ng. Chịu tr&aacute;ch nhiệm trước Hiệu trưởng v&agrave; HPCSND đối với c&ocirc;ng việc được giao.</p>\r\n\r\n<p><span style=\"color:#3498db\">8. Đ/c Phạm Thị Thanh Quỳnh &ndash; BT Đo&agrave;n thanh ni&ecirc;n.</span></p>\r\n\r\n<p>- Phụ tr&aacute;ch c&ocirc;ng t&aacute;c Đo&agrave;n TN: Lập kế hoạch c&ocirc;ng t&aacute;c cả năm, học kỳ, từng th&aacute;ng, từng tuần. L&ecirc;n kế hoạch hoạt động của chi đo&agrave;n gi&aacute;o vi&ecirc;n, bồi dưỡng thanh ni&ecirc;n ưu t&uacute;, ph&aacute;t triển Đo&agrave;n vi&ecirc;n trong nh&agrave; trường, tham gia c&ocirc;ng t&aacute;c bảo vệ m&ocirc;i trường sư phạm, ATAN nh&agrave; trường, tham gia hoạt động x&atilde; hội kh&aacute;c (tổ chức cho đo&agrave;n vi&ecirc;n hoạt động văn ho&aacute;, văn nghệ, hoạt động ngo&agrave;i giờ, c&aacute;c hội thi trong nh&agrave; trường).</p>\r\n\r\n<p><span style=\"color:#3498db\">9. C&aacute;c đồng ch&iacute;&nbsp; gi&aacute;o vi&ecirc;n.</span></p>\r\n\r\n<p>- Thực hiện nhiệm vụ nu&ocirc;i dưỡng chăm s&oacute;c, gi&aacute;o dục trẻ trong trường. Thực hiện chương tr&igrave;nh chăm s&oacute;c gi&aacute;o dục trẻ theo hướng đổi mới từng độ tuổi, thực hiện nghi&ecirc;m t&uacute;c quy chế chuy&ecirc;n m&ocirc;n v&agrave; chấp h&agrave;nh nội quy của nh&agrave; trường.</p>\r\n\r\n<p>- Đảm bảo tuyệt đối an to&agrave;n t&iacute;nh mạng cho trẻ.</p>\r\n\r\n<p>- Gương mẫu, y&ecirc;u thương, t&ocirc;n trọng v&agrave; đối xử c&ocirc;ng bằng với trẻ.</p>\r\n\r\n<p>- Thực hiện nghi&ecirc;m t&uacute;c 6 điểm tư c&aacute;ch của người c&aacute;n bộ gi&aacute;o vi&ecirc;n Hải Ph&ograve;ng.</p>\r\n\r\n<p>- Giao tiếp với phụ huynh nhẹ nh&agrave;ng, lịch sự, kết hợp với phụ huynh để tuy&ecirc;n truyền c&ocirc;ng t&aacute;c chăm s&oacute;c gi&aacute;o dục trẻ, vận động trẻ trong độ tuổi ra lớp.</p>\r\n\r\n<p>- Học tập, bồi dưỡng n&acirc;ng cao tay nghề, tr&igrave;nh độ chuy&ecirc;n m&ocirc;n, nghiệp vụ, tr&igrave;nh độ tr&ecirc;n chuẩn. Năng động, s&aacute;ng tạo trong c&ocirc;ng t&aacute;c chăm s&oacute;c gi&aacute;o dục trẻ theo hướng đổi mới h&igrave;nh thức tổ chức c&aacute;c hoạt động.</p>\r\n\r\n<p>- X&acirc;y dựng v&agrave; triển khai thực hiện Chuy&ecirc;n đề &ldquo;&ldquo;Gi&aacute;o dục vệ sinh chăm s&oacute;c v&agrave; đảm bảo an to&agrave;n cho trẻ &rdquo;, thực hiện củng cố c&aacute;c chuy&ecirc;n đề kh&aacute;c.</p>\r\n\r\n<p>- Tham gia t&iacute;ch cực c&aacute;c hoạt động phong tr&agrave;o của nh&agrave; trường, c&ocirc;ng đo&agrave;n v&agrave; đo&agrave;n thanh ni&ecirc;n tổ chức. Tham gia hội thi cấp Quận, cấp Th&agrave;nh phố đạt kết quả cao</p>\r\n\r\n<p>- Thực hiện đ&uacute;ng cam kết tr&aacute;ch nhiệm với BGH. N&ecirc;u cao &yacute; thức, tinh thần tr&aacute;ch nhiệm.</p>\r\n\r\n<p>- H&agrave;nh vi, ng&ocirc;n ngữ ứng xử của gi&aacute;o vi&ecirc;n phải mẫu mực, trang phục chỉnh tề ph&ugrave; hợp với c&aacute;c hoạt động chăm s&oacute;c, gi&aacute;o dục trẻ.</p>\r\n\r\n<p>- Duy tr&igrave; v&agrave; ph&aacute;t triển số lượng trẻ ra lớp. Chấm ăn kịp thời ch&iacute;nh x&aacute;c v&agrave; đ&ocirc;n đốc phụ huynh nộp c&aacute;c khoản thu tiền đ&uacute;ng kỳ hạn (ho&agrave;n th&agrave;nh trước ng&agrave;y 10 h&agrave;ng th&aacute;ng). Thực hiện nghi&ecirc;m t&uacute;c c&ocirc;ng t&aacute;c phổ cập trẻ em 5 Tuổi.</p>\r\n\r\n<p>- UDCNTT v&agrave;o c&ocirc;ng t&aacute;c dạy v&agrave; học.</p>\r\n\r\n<p>- Học tập n&acirc;ng cao tr&igrave;nh độ tin học, ngoại ngữ: 100% chứng chỉ B.</p>\r\n\r\n<p>- Kh&ocirc;ng tiếp kh&aacute;ch, h&uacute;t thuốc l&aacute;, uống rượu bia trong giờ l&agrave;m việc v&agrave; giờ nghỉ trưa.</p>\r\n\r\n<p>- Sau giờ học phải kiểm tra tắt điện, đ&egrave;n, quạt, nước v&agrave; đ&oacute;ng c&aacute;c cửa b&agrave;n giao BV.</p>\r\n\r\n<p><span style=\"color:#3498db\">10.&nbsp; C&aacute;c c&ocirc; nu&ocirc;i.</span></p>\r\n\r\n<p>- Nắm chắc c&aacute;ch t&iacute;nh định lượng, khẩu phần ăn cho trẻ.</p>\r\n\r\n<p>- L&agrave;m tốt c&ocirc;ng t&aacute;c tiếp phẩm, nhận thực phẩm tươi ngon c&oacute; k&yacute; hợp đồng cam kết r&otilde; r&agrave;ng. Thức ăn chế biến đảm bảo đ&uacute;ng định lượng khẩu phần ăn cho trẻ đủ chất, đủ lượng. Thay đổi m&oacute;n ăn theo thực đơn theo tuần, theo m&ugrave;a, vệ sinh sạch sẽ. Thực hiện&nbsp; đồ d&ugrave;ng, dụng cụ chế biến thực phẩm sống, ch&iacute;n ri&ecirc;ng biệt. Trong c&ocirc;ng t&aacute;c nu&ocirc;i dưỡng phải đảm bảo cho trẻ ăn đ&uacute;ng giờ, ăn ch&iacute;n uống s&ocirc;i. Thanh to&aacute;n k&yacute; giao nhận thực phẩm h&agrave;ng ng&agrave;y cụ thể r&otilde; r&agrave;ng đ&uacute;ng nguy&ecirc;n tắc. Thực phẩm h&agrave;ng ng&agrave;y của c&ocirc; kh&ocirc;ng tr&ugrave;ng với thực phẩm h&agrave;ng ng&agrave;y của trẻ. Thực hiện đảm bảo tuyệt đối c&ocirc;ng t&aacute;c ph&ograve;ng chống ch&aacute;y nổ trong nh&agrave; trường.</p>\r\n\r\n<p>- Đảm bảo tuyệt đối QTCB, VSATTP v&agrave; VS dụng cụ, MT kho, bếp.</p>\r\n\r\n<p>- Học tập, bồi dưỡng n&acirc;ng cao tay nghề, tr&igrave;nh độ CM, nghiệp vụ đạt tr&ecirc;n chuẩn.</p>\r\n\r\n<p>- Kh&ocirc;ng tiếp kh&aacute;ch, h&uacute;t thuốc l&aacute;, uống rượu bia trong giờ l&agrave;m việc v&agrave; giờ nghỉ trưa.</p>\r\n\r\n<p>- Sau giờ l&agrave;m việc phải kiểm tra tắt điện, đ&egrave;n, quạt, nước v&agrave; đ&oacute;ng cửa b&agrave;n giao BV.</p>\r\n\r\n<p><span style=\"color:#3498db\">11. Với Đ/c nh&acirc;n vi&ecirc;n y tế học đường.</span></p>\r\n\r\n<p>- Chủ động tham mưu với nh&agrave; trường về c&ocirc;ng t&aacute;c y tế học đường.</p>\r\n\r\n<p>- C&oacute; th&aacute;i độ phục vụ tận t&igrave;nh chu đ&aacute;o, lịch sự.</p>\r\n\r\n<p>- C&oacute; kế hoạch tr&igrave;nh l&ecirc;n BGH: Kế hoạch năm, th&aacute;ng, tuần, kế hoạch ph&ograve;ng chống dịch bệnh.</p>\r\n\r\n<p>- Chủ động trong kh&acirc;u đề xuất trang thết bị, thuốc&hellip;cho ph&ograve;ng y tế</p>\r\n\r\n<p>- L&agrave;m việc c&oacute; khoa học, kế hoạch, sắp xếp ph&ograve;ng y tế ngăn nắp, vệ sinh.</p>\r\n\r\n<p>- Đảm bảo tuyệt đối an to&agrave;n cho người kh&aacute;m.</p>\r\n\r\n<p>- C&oacute; nhiệm vụ chăn s&oacute;c sức khỏe ban đầu cho học sinh như: Kh&aacute;m định kỳ, sơ cứu&hellip; v&agrave; l&agrave;m tốt c&ocirc;ng t&aacute;c nha khoa cho học sinh. Phục vụ học sinh trong cả tuần. Theo d&otilde;i kiểm tra v&agrave; chịu tr&aacute;ch nhiệm ch&iacute;nh về vệ sinh an to&agrave;n thực phẩm, ăn uống của học sinh v&agrave; gi&aacute;o vi&ecirc;n.</p>\r\n\r\n<p>- Chủ động tham gia với BGH để l&agrave;m tốt c&ocirc;ng t&aacute;c ph&ograve;ng chống c&aacute;c loại dịch bệnh.</p>\r\n\r\n<p>- Phải c&oacute; HS sổ ghi ch&eacute;p theo d&otilde;i cụ thể tỉ mỉ, ch&iacute;nh x&aacute;c, khoa học theo quy định.</p>\r\n\r\n<p><span style=\"color:#3498db\">12. Đối với bảo vệ.</span></p>\r\n\r\n<p>- L&agrave; người chịu tr&aacute;ch nhiệm trước hiệu trưởng nh&agrave; trường về việc quản l&yacute; t&agrave;i sản, CSVC trong nh&agrave; trường (c&oacute; bi&ecirc;n bản hợp đồng cam kết tr&aacute;ch nhiệm cụ thể đối với từng nh&acirc;n vi&ecirc;n).</p>\r\n\r\n<p>- Quản l&yacute; t&agrave;i sản CSVC về thời gian cụ thể, thi h&agrave;nh theo văn bản giữa nh&agrave; trường v&agrave; nh&acirc;n vi&ecirc;n bảo vệ.&nbsp; B&aacute;o c&aacute;o kịp thời BGH đối với những việc xảy ra trong nh&agrave; trường.</p>\r\n\r\n<p>- Trực tiếp giải quyết những sự việc đột xuất xảy ra trong nh&agrave; trường, nh&agrave; trường gặp việc bất thường...</p>\r\n\r\n<p>- Kiểm so&aacute;t chặt chẽ việc ra v&agrave;o cơ quan của những người ngo&agrave;i v&agrave;o trường. Kh&ocirc;ng để người ngo&agrave;i tự tiện ra v&agrave;o trường. Kh&ocirc;ng được tự &yacute; cho học sinh ra khỏi trường. Khi c&oacute; người ngo&agrave;i v&agrave;o trường, bảo vệ đề nghị xuống xe, để ở vị tr&iacute; quy định, xuất tr&igrave;nh giấy tờ. Mời kh&aacute;ch v&agrave;o văn ph&ograve;ng&nbsp; gặp nh&acirc;n vi&ecirc;n trực (kh&ocirc;ng thu tiền coi xe của kh&aacute;ch đến li&ecirc;n hệ c&ocirc;ng t&aacute;c). Ghi v&agrave;o sổ kh&aacute;ch đến trường li&ecirc;n hệ c&ocirc;ng việc.</p>\r\n\r\n<p>- Chấp h&agrave;nh nghi&ecirc;m chỉnh sự chỉ đạo của Ban gi&aacute;m hiệu.</p>\r\n\r\n<p>- Ban ng&agrave;y l&agrave;m việc theo ca, ban đ&ecirc;m trực 12/12 giờ.</p>\r\n\r\n<p>- Kh&ocirc;ng cho người say, đi&ecirc;n, ăn mặc kh&ocirc;ng đứng đắn v&agrave;o trường.</p>\r\n\r\n<p>- C&oacute; tr&aacute;ch nhiệm giữ g&igrave;n trật tự, đảm bảo an to&agrave;n cho gi&aacute;o vi&ecirc;n v&agrave; học sinh trong nh&agrave; trường.</p>\r\n\r\n<p>- C&oacute; tr&aacute;ch nhiệm bảo quản to&agrave;n bộ t&agrave;i sản của nh&agrave; trường, t&agrave;i sản của gi&aacute;o vi&ecirc;n v&agrave; học sinh, nếu mất phải đền 100%.</p>\r\n\r\n<p>- C&oacute; th&aacute;i độ phục vụ kh&aacute;ch, H/s, GV, P/h lịch sự chu đ&aacute;o (kể cả qua điện thoại).</p>\r\n\r\n<p>- Sắp xếp ph&ograve;ng l&agrave;m việc gọn g&agrave;ng, ngăn nắp.</p>\r\n\r\n<p>- Sau giờ học phải kiểm tra lại điện, đ&egrave;n, quạt, nước v&agrave; đ&oacute;ng c&aacute;c cửa kịp thời, b&aacute;o c&aacute;o về BGH những lớp kh&ocirc;ng thực hiện c&aacute;c qui định tr&ecirc;n.</p>\r\n\r\n<p>- Kh&ocirc;ng tiếp kh&aacute;ch, uống rượu bia v&agrave; h&uacute;t thuốc l&aacute; trong giờ l&agrave;m việc v&agrave; nghỉ trưa.</p>\r\n\r\n<p>- Giữa 2 ca thay phi&ecirc;n nhau phải c&oacute; sự b&agrave;n giao v&agrave; ghi ch&eacute;p v&agrave;o sổ</p>\r\n', 'Hieutruong.jpg', 87, 21);
INSERT INTO `tintuc` VALUES (50, 'Lịch Nghĩ Tết Dương Lịch 2018-2019', '<p><span style=\"font-size:12px\"><span style=\"color:#000000\"><span style=\"font-family:Arial\"><span style=\"background-color:#ffffff\">Học sinh nghỉ Tết dương lịch 04 ng&agrave;y bắt đầu từ Thứ Bảy (29/12/2018) đến hết ng&agrave;y thứ Ba (1/1/2019). Nh&agrave; trường tổ chức c&aacute;c hoạt động trở lại b&igrave;nh thường v&agrave;o ng&agrave;y Thứ Tư (2/1/2019). Tr&acirc;n trọng th&ocirc;ng b&aacute;o!</span></span></span></span></p>\r\n', '<p style=\"text-align:center\"><span style=\"font-size:12px\"><span style=\"color:#000000\"><span style=\"font-family:Arial\"><span style=\"background-color:#ffffff\"><strong>TH&Ocirc;NG B&Aacute;O</strong></span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><span style=\"color:#000000\"><span style=\"font-family:Arial\"><span style=\"background-color:#ffffff\"><strong>Về việc nghỉ Tết Dương lịch năm 2019</strong></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"color:#000000\"><span style=\"font-family:Arial\"><span style=\"background-color:#ffffff\">Thực hiện c&ocirc;ng văn số 1791/SGDĐT-VP&nbsp; ng&agrave;y 13/12/2018 về việc nghỉ Tết Dương lịch năm 2019 của Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo Cần thơ. Trường mầm non V&agrave;nh Khuy&ecirc;n th&ocirc;ng b&aacute;o lịch nghỉ Tết Dương lịch năm 2019 như sau:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"color:#000000\"><span style=\"font-family:Arial\"><span style=\"background-color:#ffffff\">Học sinh nghỉ Tết dương lịch 04 ng&agrave;y bắt đầu từ Thứ Bảy (29/12/2018) đến hết ng&agrave;y thứ Ba (1/1/2019). Nh&agrave; trường tổ chức c&aacute;c hoạt động trở lại b&igrave;nh thường v&agrave;o ng&agrave;y Thứ Tư (2/1/2019). Tr&acirc;n trọng th&ocirc;ng b&aacute;o!</span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><span style=\"color:#000000\"><span style=\"font-family:Arial\"><span style=\"background-color:#ffffff\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;HIỆU TRƯỞNG</strong></span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><span style=\"color:#000000\"><span style=\"font-family:Arial\"><span style=\"background-color:#ffffff\"><strong><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img alt=\"\" src=\"http://localhost:8080/vanhkhuyen/admin/images/tintuc/hinhanhbaiviet/images/chuky.jpg\" style=\"height:109px; width:190px\" /></em></strong></span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><span style=\"color:#000000\"><span style=\"font-family:Arial\"><span style=\"background-color:#ffffff\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong></span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><span style=\"color:#000000\"><span style=\"font-family:Arial\"><span style=\"background-color:#ffffff\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Nguyễn Minh Nhựt</strong></span></span></span></span></p>\r\n', '19-11_33_28_945(1).jpg', 95, 21);
INSERT INTO `tintuc` VALUES (53, 'Thông Điệp Yêu Thương', '<p><span style=\"color:#000000\"><strong><em>&ldquo;ch&uacute;ng t&ocirc;i đặt mục ti&ecirc;u cao nhất l&agrave; qu&yacute; trọng con người, n&acirc;ng niu v&agrave; qu&yacute; mến trẻ thơ&rdquo;</em>.</strong></span></p>\r\n\r\n<p><span style=\"color:#000000\"><strong>&ldquo;H&atilde;y l&agrave;m gi&agrave;u tư duy của bạn, h&atilde;y g&oacute;p sức x&acirc;y dựng tương lai từ những vi&ecirc;n gạch đầu ti&ecirc;n bạn đặt tại Trường Mầm non V&agrave;nh Khuy&ecirc;n&rdquo;</strong></span></p>\r\n', '<p><span style=\"color:#d35400\"><strong>&nbsp;&nbsp;Thưa Q&uacute;y phụ huynh Học sinh! </strong></span></p>\r\n\r\n<p><span style=\"color:#333399\">&nbsp; &nbsp; &nbsp; &nbsp; K&iacute;nh thưa Q&uacute;y phụ huynh học sinh đang v&agrave; chuẩn bị gởi con tại Trường mầm non V&agrave;nh Khuy&ecirc;n. Con trẻ - thế hệ tương lai của đất nước v&agrave; l&agrave; niềm vui, hạnh ph&uacute;c &amp; tự h&agrave;o của mỗi gia đ&igrave;nh sẽ tung tăng đến trường học. Trường Mầm non V&agrave;nh Khuy&ecirc;n c&oacute; kh&ocirc;ng gian tho&aacute;ng đ&atilde;ng v&agrave; rộng r&atilde;i c&oacute; đủ điều kiện v&agrave; tiện nghi hiện đại nhất Việt Nam c&oacute; ngũ c&ocirc; nu&ocirc;i dạy trẻ được đ&agrave;o tạo, huấn luyện theo ti&ecirc;u chuẩn khoa học v&agrave; ti&ecirc;n tiến. Nơi đ&acirc;y chắc chắn sẽ l&agrave; địa chỉ tin cậy để Qu&yacute; Phụ huynh gởi gắm niềm tin trong việc nu&ocirc;i dưỡng thế hệ mầm non, tương lai đất nước; <strong><em>&ldquo;v&igrave; ch&uacute;ng t&ocirc;i đặt mục ti&ecirc;u cao nhất l&agrave; qu&yacute; trọng con người, n&acirc;ng niu v&agrave; qu&yacute; mến trẻ thơ&rdquo;</em></strong><strong>.</strong></span></p>\r\n\r\n<p><span style=\"color:#333399\">&nbsp; &nbsp; &nbsp; &nbsp;Ch&uacute;ng t&ocirc;i hy vọng v&agrave; rất mong muốn nhận được sự hợp t&aacute;c chăm s&oacute;c con em của c&aacute;c bậc phụ huynh đ&atilde; đồng h&agrave;nh c&ugrave;ng với ch&uacute;ng t&ocirc;i v&agrave; c&ograve;n rất nhiều Phụ huynh đang chuẩn bị gởi con v&agrave;o Trường mầm non V&agrave;nh Khuy&ecirc;n; Lần đầu ti&ecirc;n tại Hậu Giang-Trường mầm non V&agrave;nh Khuy&ecirc;n ch&uacute;ng t&ocirc;i &aacute;p dụng cho c&aacute;c B&eacute; y&ecirc;u được sinh hoạt trong <strong>Ng&ocirc;i nh&agrave; lớn Montessori<em> &ldquo;Nghệ thuật nu&ocirc;i dạy trẻ đỉnh cao&rdquo;</em>.</strong> Con c&aacute;i Qu&yacute; vị đến Trường Mầm non V&agrave;nh Khuy&ecirc;n kh&ocirc;ng chỉ được học h&agrave;nh, vui chơi, được chăm s&oacute;c chu đ&aacute;o từ miếng ăn, giấc ngủ để khỏe mạnh, trưởng th&agrave;nh; m&agrave; điều quan trọng hơn l&agrave; con c&aacute;i Qu&yacute; vị c&ograve;n được V&agrave;nh Khuy&ecirc;n truyền thụ những tri thức mới, tr&ograve; chơi ph&aacute;t triển tr&iacute; tuệ để c&aacute;c b&eacute; th&ocirc;ng minh, năng động v&agrave; s&aacute;ng tạo hơn.</span></p>\r\n\r\n<p><span style=\"color:#333399\">&nbsp; &nbsp; &nbsp; &nbsp;Tr&acirc;n trọng k&iacute;nh mời Q&uacute;y phụ huynh học sinh đến tham quan Ng&ocirc;i nh&agrave; lớn Montessori <strong><em>&ldquo;nghệ thuật nu&ocirc;i dạy trẻ đỉnh cao&rdquo; </em>tại Hậu Giang v&agrave; chỉ c&oacute;<em> </em>ở<em> </em></strong>Trường mầm non V&agrave;nh Khuy&ecirc;n ch&uacute;ng t&ocirc;i.</span></p>\r\n\r\n<p><span style=\"color:#333399\">&nbsp; &nbsp; &nbsp; Nhằm tạo điều kiện gi&uacute;p đỡ cho Q&uacute;y phụ huynh học sinh gởi con tại Trường mầm non V&agrave;nh Khuy&ecirc;n;&nbsp; Năm học 2019 -2020 với mức học ph&iacute; B&eacute; y&ecirc;u ưu đ&atilde;i nhất như sau :</span></p>\r\n\r\n<p><span style=\"color:#333399\"><strong>-&nbsp;</strong><strong>Nh&agrave; trẻ tiền ăn &amp; học ph&iacute; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : 2.200.000vnđ/th&aacute;ng</strong></span></p>\r\n\r\n<p><span style=\"color:#333399\"><strong>- Mẫu gi&aacute;o tiền ăn &amp; học ph&iacute; &nbsp; : 2.400.000vnđ/th&aacute;ng</strong></span></p>\r\n\r\n<p><span style=\"color:#333399\">Q&uacute;y Phụ huynh vui l&ograve;ng&nbsp; đăng k&yacute; nhập học từ ng&agrave;y 01/7/2019 đến 20/8/2019.&nbsp;</span></p>\r\n\r\n<p><span style=\"color:#333399\">&nbsp;Q&uacute;y phụ huynh học sinh li&ecirc;n hệ để được tư vấn :</span></p>\r\n\r\n<p><span style=\"color:#333399\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Điện thoại: 0909090909</span></p>\r\n\r\n<p><span style=\"color:#333399\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (C&ocirc; Ngọc Ng&acirc;n &amp; Kim Ngọc)</span></p>\r\n\r\n<p><span style=\"color:#333399\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tr&acirc;n trọng cảm ơn Q&uacute;y phụ huynh học sinh !</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color:#d35400\"> <span style=\"font-size:14px\">Mầm Non V&agrave;nh Khuy&ecirc;n </span></span></strong></p>\r\n\r\n<p><span style=\"color:#d35400\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;NƠI ƯƠM MẦM TƯƠNG LAI &nbsp;</strong></span></p>\r\n', 'e06d061a77a7bde916b8a91163029d41_1470134034.jpg', 111, 21);
INSERT INTO `tintuc` VALUES (54, 'Trang Thiết Bị', '<div>Tổng diện t&iacute;ch khu&ocirc;n vi&ecirc;n trường : 9.465m<sup>2</sup>, với 15 lớp học rộng r&atilde;i, tho&aacute;ng m&aacute;t. Mỗi ph&ograve;ng&nbsp;&nbsp;học 60m<sup>2</sup>&nbsp;v&agrave; c&oacute; 02&nbsp;ph&ograve;ng chức năng<br />\r\nNgo&agrave;i c&aacute;c lớp học của trẻ, trường c&ograve;n x&acirc;y dựng c&aacute;c ph&ograve;ng chức năng gồm:</div>\r\n\r\n<ul>\r\n</ul>\r\n', '<p>Trường Mầm Non V&agrave;nh Khuy&ecirc;n l&agrave; trường mẫu gi&aacute;o c&ocirc;ng lập. Thuộc địa b&agrave;n khu phố 4,&nbsp;phường Ph&uacute; Mỹ, Tx.Ng&atilde; Bảy, Hậu Giang.</p>\r\n\r\n<p>C&aacute;c ph&ograve;ng chức năng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>a) Khối ph&ograve;ng nh&oacute;m trẻ, lớp mẫu gi&aacute;o:</p>\r\n\r\n<p>- Ph&ograve;ng sinh hoạt chung: đảm bảo diện t&iacute;ch trung b&igrave;nh cho một trẻ theo quy định tại Điều lệ trường mầm non. Trang bị đủ b&agrave;n ghế cho gi&aacute;o vi&ecirc;n v&agrave; trẻ, đủ đồ d&ugrave;ng, đồ chơi, học liệu cho trẻ hoạt động; c&oacute; tranh ảnh, hoa, c&acirc;y cảnh trang tr&iacute; đẹp, ph&ugrave; hợp. Tất cả đồ d&ugrave;ng, thiết bị phải đảm bảo theo quy c&aacute;ch do Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo quy định;</p>\r\n\r\n<p>- Ph&ograve;ng ngủ: đảm bảo diện t&iacute;ch trung b&igrave;nh cho một trẻ theo quy định tại Điều lệ trường mầm non. C&oacute; đầy đủ c&aacute;c đồ d&ugrave;ng phục vụ trẻ ngủ;</p>\r\n\r\n<p>- Ph&ograve;ng vệ sinh: đảm bảo diện t&iacute;ch trung b&igrave;nh cho một trẻ v&agrave; c&aacute;c y&ecirc;u cầu theo quy định tại Điều lệ trường mầm non, được x&acirc;y kh&eacute;p k&iacute;n hoặc gần với nh&oacute;m lớp, thuận tiện cho trẻ sử dụng, trung b&igrave;nh 10 trẻ c&oacute; 1 bồn cầu vệ sinh; chỗ đi ti&ecirc;u, đi tiểu được ngăn c&aacute;ch bằng v&aacute;ch ngăn lửng cao 1,2m. Đối với trẻ nh&agrave; trẻ dưới 24 th&aacute;ng trung b&igrave;nh 4 trẻ c&oacute; 1 ghế ngồi b&ocirc;. C&oacute; đủ nước sạch, bồn rửa tay c&oacute; v&ograve;i nước v&agrave; x&agrave; ph&ograve;ng rửa tay. C&aacute;c thiết bị vệ sinh bằng men sứ, k&iacute;ch thước ph&ugrave; hợp với trẻ;</p>\r\n\r\n<p>- Hi&ecirc;n chơi (vừa c&oacute; thể l&agrave; nơi tổ chức ăn trưa cho trẻ): thuận tiện cho c&aacute;c sinh hoạt của trẻ khi mưa, nắng; đảm bảo quy c&aacute;ch v&agrave; diện t&iacute;ch trung b&igrave;nh cho một trẻ theo quy định tại Điều lệ trường mầm non. Lan can của hi&ecirc;n chơi c&oacute; khoảng c&aacute;ch giữa c&aacute;c thanh gi&oacute;ng đứng kh&ocirc;ng qu&aacute; 0,1m.</p>\r\n\r\n<p>b) Khối ph&ograve;ng phục vụ học tập:</p>\r\n\r\n<p>- Ph&ograve;ng gi&aacute;o dục thể chất, nghệ thuật: c&oacute; diện t&iacute;ch tối thiểu 60 m2, c&oacute; c&aacute;c thiết bị, đồ d&ugrave;ng ph&ugrave; hợp với hoạt động ph&aacute;t triển thẩm mỹ v&agrave; thể chất của trẻ (đồ chơi &acirc;m nhạc, quần &aacute;o, trang phục, đạo cụ m&uacute;a, gi&aacute; vẽ, v&ograve;ng tập...).</p>\r\n\r\n<p>c) Khối ph&ograve;ng tổ chức ăn:</p>\r\n\r\n<p>- Khu vực nh&agrave; bếp đảm bảo diện t&iacute;ch trung b&igrave;nh cho một trẻ theo quy định tại Điều lệ trường mầm non; được x&acirc;y dựng theo quy tr&igrave;nh vận h&agrave;nh một chiều theo tr&igrave;nh tự: nơi sơ chế, nơi chế biến, bếp nấu, chỗ chia thức ăn. Đồ d&ugrave;ng nh&agrave; bếp đầy đủ, vệ sinh v&agrave; được sắp xếp ngăn nắp, thuận tiện khi sử dụng;</p>\r\n\r\n<p>- Kho thực phẩm c&oacute; ph&acirc;n chia th&agrave;nh khu vực để c&aacute;c loại thực phẩm ri&ecirc;ng biệt, đảm bảo c&aacute;c quy định về vệ sinh an to&agrave;n thực phẩm;</p>\r\n\r\n<p>- C&oacute; tủ lạnh lưu mẫu thức ăn.</p>\r\n\r\n<p>d) Khối ph&ograve;ng h&agrave;nh ch&iacute;nh quản trị:</p>\r\n\r\n<p>- Văn ph&ograve;ng trường: diện t&iacute;ch tối thiểu 30m2, c&oacute; b&agrave;n ghế họp v&agrave; tủ văn ph&ograve;ng, c&aacute;c biểu bảng theo quy định của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo;</p>\r\n\r\n<p>- Ph&ograve;ng hiệu trưởng: diện t&iacute;ch tối thiểu 15m2, c&oacute; đầy đủ c&aacute;c phương tiện l&agrave;m việc v&agrave; b&agrave;n ghế tiếp kh&aacute;ch;</p>\r\n\r\n<p>- Ph&ograve;ng c&aacute;c ph&oacute; hiệu trưởng: diện t&iacute;ch v&agrave; trang bị phương tiện l&agrave;m việc như ph&ograve;ng hiệu trưởng;</p>\r\n\r\n<p>- Ph&ograve;ng h&agrave;nh ch&iacute;nh quản trị: diện t&iacute;ch tối thiểu 15m2, c&oacute; m&aacute;y vi t&iacute;nh v&agrave; c&aacute;c phương tiện l&agrave;m việc;</p>\r\n\r\n<p>- Ph&ograve;ng y tế: diện t&iacute;ch tối thiểu 12m2, c&oacute; c&aacute;c trang thiết bị y tế v&agrave; đồ d&ugrave;ng theo d&otilde;i sức khoẻ trẻ, c&oacute; bảng th&ocirc;ng b&aacute;o c&aacute;c biện ph&aacute;p t&iacute;ch cực can thiệp chữa bệnh v&agrave; chăm s&oacute;c trẻ suy dinh dưỡng, trẻ b&eacute;o ph&igrave;; c&oacute; bảng kế hoạch theo d&otilde;i ti&ecirc;m ph&ograve;ng v&agrave; kh&aacute;m sức khoẻ định kỳ cho trẻ; c&oacute; tranh ảnh tuy&ecirc;n truyền chăm s&oacute;c sức khoẻ, ph&ograve;ng bệnh cho trẻ;</p>\r\n\r\n<p>- Ph&ograve;ng bảo vệ, thường trực: diện t&iacute;ch tối thiểu 6m2; c&oacute; b&agrave;n ghế, đồng hồ, bảng, sổ theo d&otilde;i kh&aacute;ch;</p>\r\n\r\n<p>- Ph&ograve;ng d&agrave;nh cho nh&acirc;n vi&ecirc;n: diện t&iacute;ch tối thiểu 16m2, c&oacute; tủ để đồ d&ugrave;ng c&aacute; nh&acirc;n;</p>\r\n\r\n<p>- Khu vệ sinh cho gi&aacute;o vi&ecirc;n, c&aacute;n bộ, nh&acirc;n vi&ecirc;n: diện t&iacute;ch tối thiểu 9m2; c&oacute; đủ nước sử dụng, c&oacute; bồn rửa tay v&agrave; buồng tắm ri&ecirc;ng;</p>\r\n\r\n<p>- Khu để xe cho gi&aacute;o vi&ecirc;n, c&aacute;n bộ, nh&acirc;n vi&ecirc;n đảm bảo an to&agrave;n, tiện lợi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c lớp học rộng r&atilde;i, tho&aacute;ng m&aacute;t tạo kh&ocirc;ng gian tối đa cho sinh hoạt v&agrave; chăm s&oacute;c trẻ tr&agrave;n ngập &aacute;nh s&aacute;ng tự nhi&ecirc;n.<br />\r\n100% c&aacute;c ph&ograve;ng được thiết kế v&agrave; x&acirc;y dựng theo chuẩn quốc gia, được trang&nbsp;ti vi kết nối internet, đ&agrave;n, đồ d&ugrave;ng đồ chơi đầy đủ theo quy định của ng&agrave;nh học mầm non.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://localhost:8080/vanhkhuyen/admin/images/tintuc/hinhanhbaiviet/images/IMG_5130_1277231260.jpg\" style=\"height:403px; width:800px\" /><br />\r\n<br />\r\nC&aacute;c ph&ograve;ng học kh&eacute;p k&iacute;n, diện t&iacute;ch 60m<sup>2</sup>&nbsp; với hệ thống camera nhằm đảm bảo an to&agrave;n cho c&aacute;c ch&aacute;u một c&aacute;ch to&agrave;n diện từ ng&agrave;y đầu đến trường<br />\r\n<br />\r\nĐồ d&ugrave;ng, đồ chơi được trang bị đầy đủ, v&agrave; lựa chon theo quy chuẩn của bộ GD&amp;ĐT ph&ugrave; hợp với đặc điểm ph&aacute;t triển của trẻ theo từng độ tuổi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://localhost:8080/vanhkhuyen/admin/images/tintuc/hinhanhbaiviet/images/lap-dat-camera-truong-mam-non-binh-minh.jpg\" style=\"height:394px; width:700px\" /></p>\r\n', 'sử-dụng-điều-hòa-tiết-kiệm-điện.jpg', 122, 21);
INSERT INTO `tintuc` VALUES (55, 'Điều Kiện Sinh Hoạt', '<p>Diện t&iacute;ch s&acirc;n chơi được quy hoạch, thiết kế ph&ugrave; hợp. C&oacute; c&acirc;y xanh, thường xuy&ecirc;n được chăm s&oacute;c, cắt tỉa đẹp, tạo b&oacute;ng m&aacute;t s&acirc;n trường.</p>\r\n', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://localhost:8080/vanhkhuyen/admin/images/tintuc/hinhanhbaiviet/images/images954584_DSC_1092.jpg\" style=\"height:530px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Diện t&iacute;ch s&acirc;n chơi được quy hoạch, thiết kế ph&ugrave; hợp. C&oacute; c&acirc;y xanh, thường xuy&ecirc;n được chăm s&oacute;c, cắt tỉa đẹp, tạo b&oacute;ng m&aacute;t s&acirc;n trường. C&oacute; vườn c&acirc;y d&agrave;nh ri&ecirc;ng cho trẻ chăm s&oacute;c, bảo vệ c&acirc;y cối v&agrave; tạo cơ hội cho trẻ kh&aacute;m ph&aacute;, học tập. Khu vực trẻ chơi được l&aacute;t gạch (hoặc l&aacute;ng xi măng) v&agrave; trồng thảm cỏ, c&oacute; &iacute;t nhất 5 loại thiết bị v&agrave; đồ chơi ngo&agrave;i trời. S&acirc;n vườn thường xuy&ecirc;n sạch sẽ, c&oacute; r&agrave;o chắn an to&agrave;n ngăn c&aacute;ch với ao, hồ. &nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://localhost:8080/vanhkhuyen/admin/images/tintuc/hinhanhbaiviet/images/mo-truong-mam-non-tu-thuc-min-4.jpg\" style=\"float:right; height:233px; width:453px\" /><img alt=\"\" src=\"http://localhost:8080/vanhkhuyen/admin/images/tintuc/hinhanhbaiviet/images/P_20160617_074137%20-%20Copy(2).jpg\" style=\"float:left; height:233px; width:451px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n', 'mo-truong-mam-non-tu-thuc-min-4.jpg', 122, 21);
INSERT INTO `tintuc` VALUES (56, 'Thông Báo Tuyển Sinh Năm Học 2019-2020', '', '<div class=\"dk-post-detail\">\r\n<h2 style=\"text-align:center\"><span style=\"color:#ff0000\"><strong>TH&Ocirc;NG B&Aacute;O</strong></span></h2>\r\n\r\n<h2 style=\"text-align:center\">Tuyển sinh năm học 2019-2020</h2>\r\n\r\n<p>- Căn cứ v&agrave;o kế hoạch tuyển sinh năm học 2019 &ndash; 2020 đ&atilde; được Ph&ograve;ng gi&aacute;o dục v&agrave; đ&agrave;o tạo T&iacute;nh Hậu Giang ph&ecirc; duyệt ;</p>\r\n\r\n<p>- Căn cứ nhu cầu gởi trẻ của Q&uacute;y phụ huynh ;<span style=\"color:#0000ff\"><strong>Trường Mầm non V&agrave;nh Khuy&ecirc;n</strong></span>&ndash; Trường đạt chuẩn Quốc gia th&ocirc;ng b&aacute;o tuyển sinh năm học 2019 -2020 như sau :</p>\r\n\r\n<p><strong>I. ĐỐI TƯỢNG :</strong></p>\r\n\r\n<p>&nbsp; - C&aacute;c b&eacute; từ <strong>12 th&aacute;ng tuổi đến 05 tuổi</strong> ;</p>\r\n\r\n<p>&nbsp; - Lớp học : 20 b&eacute;/lớp</p>\r\n\r\n<p><strong>&nbsp;</strong><strong>Qu&yacute; phụ huynh vui l&ograve;ng li&ecirc;n hệ :&nbsp; </strong></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Ph&ograve;ng h&agrave;nh ch&iacute;nh &ndash; Bộ phận tư vấn tuyển sinh :</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>&nbsp;Li&ecirc;n hệ</strong> : C&ocirc; Ngọc Ng&acirc;n &amp; C&ocirc; Kim Ngọc</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <strong>&nbsp;Hotline</strong> &nbsp;: 0292.3767179 &nbsp;&amp;&nbsp; 0907.190054</p>\r\n\r\n<p><strong>II. TIỀN ĂN &amp; HỌC PH&Iacute;&nbsp; : </strong>(25 ng&agrave;y/th&aacute;ng)&nbsp;</p>\r\n\r\n<p><strong>&nbsp;</strong><span style=\"color:#ff0000\">■ Học từ thứ 2 đến thứ 7</span> :</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - Nh&oacute;m Nh&agrave; trẻ&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;:&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Nh&oacute;m Mẫu gi&aacute;o &nbsp;&nbsp;&nbsp; &nbsp;&n